tinymce.PluginManager.add("example",function(d,c){d.addButton("example",{text:"My button",icon:!1,onclick:function(){d.windowManager.open({title:"Example plugin",body:[{type:"textbox",name:"title",label:"Title"}],onsubmit:function(a){d.insertContent("Title: "+a.data.title)}})}}),d.addMenuItem("example",{text:"Example plugin",context:"tools",onclick:function(){d.windowManager.open({title:"TinyMCE site",url:c+"/dialog.html",width:600,height:400,buttons:[{text:"Insert",onclick:function(){var a=d.windowManager.getWindows()[0];d.insertContent(a.getContentWindow().document.getElementById("content").value),a.close()}},{text:"Close",onclick:"close"}]})}})});